INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('2cbf4f14-b10c-4f8a-ac4c-329126845cf0', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Dia 2- BANGKOK (Desayuno).', 1);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('16124e70-104d-4132-b4ce-87f342448265', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Después del desayuno, Visita a tres de los templos budistas más populares de la ciudad. Empezaremos por el Wat Traimit, situado en el extremo de Chinatown, en Yaowarat Road, cerca de la estación de tren Hualampong. Wat Traimit alberga el Buda de oro macizo más grande del mundo, midiendo casi cinco metros de altura con un peso de cinco toneladas y media. La excursión continuará hacia Wat Pho, el templo más grande de Bangkok. El templo del enorme Buda reclinado y los Chedis de los Reyes. Este se encuentra detrás del Templo del Buda. Es uno de los mayores templos de la ciudad y famoso por su gigantesco Buda reclinado que mide 46 metros de largo y está cubierto de oro. A continuación, visitarán el Palacio Real, que es sin duda, el monumento más famoso de la ciudad. Construido en 1782, por 150 años fue la casa del rey de Tailandia, la corte real y la sede administrativa del gobierno. El Gran Palacio de Bangkok es un edificio antiguo que continua impresionando a sus visitantes por su hermosa arquitectura y detalles. Dentro del complejo, se encuentra Wat Phra Kaew o el Templo del Buda Esmeralda (oficialmente conocido como Wat Phra Sri Rattana Satsadaram), considerado como el templo budista más importante de Tailandia, consagra el Buda más reverenciado tallado en un solo bloque de jade.', 2);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('39f7d0cb-853b-40cf-9e6e-0b35c18fa559', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Traslados incluidos.', 3);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('a1c2cd88-ad86-4d9e-a888-eee57e6c8c02', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Alojamiento en hotel', 4);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('66f1d1c0-4ea0-4b92-99a3-1ab784dc96ed', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Día 3 – BANGKOK – CHIANG RAI (Desayuno, Almuerzo y cena).', 5);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('dc3e1b04-3e58-461b-b8e7-6d36ccbbda02', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Después del desayuno traslado al aeropuerto para tomar el vuelo hacia Chiang Rai', 6);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('dca9a26d-147a-4e32-bf33-0b5c1f81f261', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Llegada a Chiang Rai con el vuelo no más tarde que las 10.00 AM.', 7);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('9a7a459b-95f6-4259-994c-04fb44098591', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Proceder a Chui Fong, una hermosa cultivadora de té. En un ambiente rodeado de plantaciones en las laderas de pequeñas colinas, podrán disfrutar de varias delicias como helado de té, torta de té (a gasto propio) y un almuerzo en el restaurante local cercano. Próximamente, pasaremos por un museo de opio antes de salir hacia el distrito de Mae Chan al norte de Chiang Rai. Luego continuaremos a pie, subiendo por las colinas a través de calles angostas para visitar una aldea de las minorías étnicas Akha y Yao. Nuestro último destino del día será una de las aldeas Karen, donde conoceremos a esta famosa tribu montañera – el grupo étnico minoritario más grande de Tailandia. Esta tribu originaria de Tíbet emigró a lo que hoy se conoce como Myanmar hace unos dos mil años. Durante el siglo XVIII, se estima que el conflicto político y la persecución fomentaron su migración masiva hacia el norte de Tailandia, donde todavía al día de hoy se encuentran sin un estado residencial oficial. La tribu es más reconocida por las mujeres Kayan – un subgrupo de los Po (Karen rojo) – cuyos cuellos están adornados en anillos de latón. A veces hasta empezando desde los cinco años, la presión constante empuja sus clavículas y costillas hacia abajo, dando la impresión de un cuello extendido; la razón detrás de su apodo ‘mujeres jirafa’ – una vista asombrosa. Al concluir, traslado de regreso al hotel.', 8);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('7f14d64c-ee16-413e-b6bc-72e36cab67f0', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 'Cena y alojamiento en hotel.', 9);
