INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('5cbcaba1-7629-4531-9d1f-d46b752b803c', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Dia 2- BANGKOK (Desayuno).', 1);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('3ac4098b-157f-4503-88ca-6c5be9480072', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Después del desayuno, Visita a dos de los templos budistas más inusuales de la ciudad. Empezaremos por Wat Traimit, situado en el extremo de Chinatown en la calle Yaowarat, cerca de la estación de tren Hualampong. Wat Traimit alberga el Buda de oro macizo más grande del mundo, midiendo casi cinco metros de altura con un peso de cinco toneladas y media. La excursión continuará hacia Wat Pho, el templo más grande de Bangkok, donde se encuentra el enorme Buda reclinado y los Chedis de los Reyes. Es uno de los mayores templos de la ciudad, famoso por su gigantesco Buda reclinado que mide 46 metros de largo y está cubierto de oro.', 2);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('39758cdd-9d23-4ab9-8355-17355af6fdbe', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Traslados incluidos.', 3);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('ef9ed93c-9f3e-46e4-b3c9-2fcd548531d0', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Alojamiento en el hotel.', 4);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('12c00f6b-c78c-4dcf-84e4-9fa2622952bb', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'OPCIONAL: Grand Palace', 5);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('6e164d62-bc80-429d-937e-f06a194c1608', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'El Gran Palacio es uno de los monumentos más famoso de la ciudad. Construido en 1782, sirvió por 150 años como la casa del rey de Tailandia, la corte real y la sede administrativa del gobierno. El Gran Palacio de Bangkok es un edificio antiguo que continúa impresionando a sus visitantes con su hermosa arquitectura y detalles intricados. Dentro del complejo, se encuentra Wat Phra Kaew – ‘el Templo del Buda Esmeralda’ (oficialmente Wat Phra Sri Rattana Satsadaram) – considerado el templo budista más importante de Tailandia. Es aquí que se consagra la imagen de buda más reverenciada del país, meticulosamente tallada en un solo bloque de jade.', 6);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('4e0eb2cc-11b5-4f0b-9665-abf5cad3404b', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Neto por persona, USD', 7);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('9ae1c08a-1afc-446e-8d84-d6a69153412d', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'USD 25', 8);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('4c011d2a-a3f4-4614-8739-d0f3d357f7cc', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Día 3 – BANGKOK – AYUTTHAYA – ANG THONG – SUKHOTHAI (Desayuno y Almuerzo).', 9);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('8abc1e44-53e7-46b2-8625-69f7698bc10d', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Después del desayuno, salida desde Bangkok hacia el norte.', 10);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('5704a706-80b5-492c-b3b3-886327846667', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Haremos la primera parada en la ciudad de Ayutthaya – la antigua capital del reino de Siam y centro arqueológico del país – donde visitaremos sus templos y restos de sus fortificaciones más icónicas. Declarada como patrimonio de la humanidad por la UNESCO en 1991, Ayutthaya permanece un lugar mágico e imperdible para los amantes de la historia y civilizaciones antiguas.', 11);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('a8ee7899-e8b7-4d15-a54b-d2bfad97f504', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Próximamente, nos dirigiremos hacia la ciudad de Ang Thong donde podremos visitar el templo Wat Muang, famoso debido a que alberga la estatua de buda sentado más grande de Tailandia (novena mayor del mundo) con casi 100 metros de altura.', 12);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('14a83cd8-fa1f-425c-9c3f-136658b32b5d', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Continuación hacia Sukhothai – Almuerzo, paisajes naturales', 13);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('c4f5a60b-22b9-448c-978e-cf349414717c', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Durante el trayecto seremos testigos del cambio en el paisaje, que va enverdeciendo y poniendose cada vez más frondoso; adelantando ya los paisajes selváticos que cubren la mayoría del norte del país.', 14);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('f7f9b889-0092-46fd-9728-9ad7e9e0de92', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Llegaremos al alojamiento en Sukhothai por la tarde.', 15);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('669f8c8a-8aab-402a-b019-12ff0b3b86a3', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Día 4 – SUKHOTHAI – CHIANG RAI (Desayuno y Almuerzo).', 16);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('9e1b751c-cfe2-473f-8e11-92886e4142f9', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Desayuno en el hotel.', 17);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('5675b04a-5891-49e7-8be0-fe2d7dddfe0d', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'El punto más destacado del día de hoy es el Parque Arqueológico de Sukhothai, declarado como un Patrimonio de la Humanidad por UNESCO debido a su increíble belleza y exposición de varios siglos de prosperidad de la civilización tailandesa (demostrado a través de los gran monumentos y templos sofisticados erigidos en la zona).', 18);
INSERT INTO public.exclusions (id, destination_id, item, display_order) VALUES ('0489549b-662b-490a-ae9d-9f9309bbf6aa', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 'Una vez finalizada visita, nos dirigiremos a Chiang Rai vía Lampang mientras disfrutamos de las maravillosas vistas y el famoso lago Payao.', 19);
