INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('58e4e592-9b23-40ef-8d53-0cd916ca01a1', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Opcionalmente ¡Prepárate para una emocionante aventura! horas, tu guía turístico privado te recibirá en tu hotel. Desde allí, te dirigirás al Quad Bike Centre, que se encuentra a unos 15 minutos de viaje. Después de una breve pero importante orientación sobre seguridad e instrucciones de conducción, comenzará tu aventura en quad en Sharm. Tomarás el control de tu quad y partirás a través del fascinante desierto del Sinaí, donde podrás disfrutar de impresionantes vistas del paisaje. 20 Después de aproximadamente una hora de emocionante conducción, llegarás al punto más alto de tu viaje. Aquí, tendrás la oportunidad de tomar un merecido descanso y saborear un té de hierbas beduino tradicional, sumergiéndote en la auténtica cultura de la región.', 10);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('aeb4d736-e027-4414-b4f5-cabff3c080a4', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Una vez que hayas recargado energías,. Desde allí, serás trasladado de regreso a tu hotel en la comodidad de un vehículo con aire acondicionado. La noche es para que la disfrutes a tu aire.', 11);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('fb9aec83-5585-4a2e-b1e9-7746616c11c8', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Disfruta de una tranquila noche en tu hotel de Sharm, donde podrás reflexionar sobre la emocionante aventura del día.', 12);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('3055de3b-214f-4546-8a42-44a415ce8618', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Día 6 – QUAD BIKE EN SHAM EL SHEIHK (Desayuno).', 13);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('7ee85cda-f963-4234-82bb-cba1381aaf51', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Desayuno, día libre', 14);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('477a3304-160b-4afa-94fa-d98c5d434561', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Opcionalmente, salida de su hotel. Lo llevarán al muelle marítimo, donde se embarcará en un barco con destino al Parque Nacional Ras Mohamed. Este extraordinario santuario marino es un paraíso para los entusiastas de las actividades submarinas, con más de 130 especies de arrecifes de coral y la asombrosa cantidad de 1000 variedades de peces vibrantes y coloridos. Durante su viaje, el barco hará tres paradas, lo que le brindará la oportunidad de participar en aventuras de natación y esnórquel en las aguas cristalinas del Mar Rojo. Se servirá un delicioso almuerzo a bordo mientras disfruta de la belleza de este mundo submarino. Al final del día, el barco regresará a la actividad de esnórquel en el Mar Rojo y lo transportarán de regreso a su hotel. Disfrute de otra cómoda estadía de una noche en su hotel de Sharm El Sheikh..', 15);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('b8d46f76-1c79-41fc-b6bb-1a772087d7a8', '4a457002-eba2-4864-96a1-0a7039b379d5', 'Día 7 –EL CAIRO (Desayuno).', 16);
