INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('87ae8e1a-e675-4f68-9f32-3b697ec99269', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Opcional visitando la pirámide escalonada en Sakkara, diseñada por el arquitecto Imhotep para el rey Zoser. Almuerzo en un restaurante local de buena calidad.', 1);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('913a8d40-4cb4-492f-8948-c3b948f0c4be', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Día 3 – EL CAIRO –LUXOR - El Templo de Karnak - Templo de Luxor  (Pensión Completa).', 2);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('d41880c3-1e10-47c5-8f27-2fc33b901b63', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Desayuno. Salida según plan de vuelos. Tomar el vuelo con destino al Luxor. Llegada a Luxor Traslado en vehículo privado con aire acondicionado al Crucero por el Nilo. Donde te recibirán y trasladarán para embarcar en un crucero de lujo de 5 estrellas por el Nilo. Pensión completa.', 3);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('2a160c06-e946-42e5-ba01-2213d2ef3ff5', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Salida para visitar El Templo de Karnak erigido en honor al dios Amón y principal recinto de culto en Egipto desde las dinastías que conformaron el Imperio Nuevo y que cuenta, entre otros, con una magnífica sala hipóstila de 23 metros de altura que alberga en su recinto nada menos que 134 columnas y el Templo de Luxor, más pequeño que el de Karnak y comunicado con éste por una impresionante avenida de esfinges con cabeza de carnero, más conocida como la Avenida de las esfinges. Regreso a la motonave, y noche a bordo.', 4);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('0244a70e-b754-4a94-924c-b9f62074e625', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Día 4 – LUXOR - ESNA - EDFU (Pensión Completa).', 5);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('3cd36a9a-c489-4a66-8869-f36de53b38ca', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Pensión completa. Desayuno. Salida para Visitar: el Valle de los Reyes, declarado por la Unesco Patrimonio de la Humanidad y que alberga la mayoría de las tumbas de los faraones del Imperio Nuevo. El Templo de la Reina Hatshepsut, que fue la primera mujer en ocupar la posición de faraón; el templo, situado en Deir El Bahari, es uno de los pocos templos construidos directamente en la roca y consta de varias terrazas que ejercían como vestíbulos para albergar a multitud de personas durante las festividades religiosas. Los Colosos de Memnon, gigantescas estatuas que representan al faraón Amenhotep III y que permanecen en posición sedente desde hace más de 3.500 años. Regreso a la motonave, navegación hacia Edfú y noche a bordo.', 6);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('c9964ef6-4262-40e3-a8e1-0d5c709d6e1a', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Día 5 – EDFU - KOM OMBO – ASWAN (Pensión Completa).', 7);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('80b6f192-e884-4f67-ae69-0669a1ce54c5', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Pensión completa. Desayuno. Salida para realizar la visita al Templo de Edfú, erigido en honor del dios Horus con cabeza de halcón. El templo, situado en la orilla occidental del río Nilo es uno de los más grandes de Egipto y el mejor conservado hasta el momento. Las inscripciones en sus paredes proporcionan información importante sobre el lenguaje, la mitología y la religión durante el periodo grecorromano. Regreso a la motonave y navegación.', 8);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('f62dff9d-dbce-4367-a79c-4d5f8e7f1ee7', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Llegada a Kom Ombo y salida para realizar la visita del único templo en Egipto dedicado simultáneamente a dos dioses, el dios Sobek con cabeza de cocodrilo y el dios Haroeris con cabeza de halcón. Regreso a la motonave, navegación y noche a bordo.', 9);
