INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('a7f93dee-faa0-4a9f-806d-ce0095e47078', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 'Opcional: Salida para visitar el Barrio Copto donde se encuentra la Iglesia de Santa María la Virgen, más conocida como la Iglesia Colgante, la Iglesia de San Jorge y la Iglesia de San Sergio. Continuación en el Museo de Arte Egipcio, que alberga numerosas estatuas, pinturas, relieves, elementos funerarios y otros muchos objetos de la época de los Faraones; la Mezquita de Mohamed Ali Pasha, más conocida como la Mezquita de Alabastro, la Ciudadela de Salah El aDin (Saladino, quien fuera Sultán de Egipto), declarada Patrimonio de la Humanidad y el Gran Bazar de Khan el Khalili donde podremos callejear para realizar compras o tomar un té en un café de la zona. Traslado al hotel y alojamiento.', 18);
