INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('10a7cc9e-8b84-45ea-a825-91bff38faf6a', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Después del almuerzo, tendremos una experiencia con las flores y su tradicional forma de adornarlas. Visita a una floristería artesanal para ver cómo se realizan los ramos, cómo se transmite y conserva este hermoso y tradicional arte espiritual de Vietnam que ha llegado a nuestros días generación tras generación. Los invitados aprenderán a reconocer flores exóticas a la vez que disfrutan preparando una ofrenda floral espiritual. Posteriormente llegamos al lago Hoan Kiem, el corazón de Hanói, donde daremos un paseo alrededor del lago con una vista panorámica al templo Ngoc Son, situado en medio del lago, junto con el puente rojo The Huc.Por último, realizaremos un paseo panorámico en ciclo pousse por el Barrio Antiguo de Hanói, también conocido como el barrio de las 36 calles ya que en su tiempo fue conocido por el oficio de los artesanos que las habitaban y por los talleres que allí había.', 2);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('6c509cac-f5d3-4ddc-bd56-3719b2fc8b3a', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Regreso al hotel y alojamiento en Hanói.', 3);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('a067b480-54a6-41ba-a4b4-79ce682926a4', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Régimen de comidas: Desayuno y almuerzo', 4);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('04f28601-e473-48a5-8dbe-4ad528eacca1', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Recomendaciones: Reservar un masaje tradicional o participar en el curso de cocina vietnamita. Asistencia al espectáculo Marionetas sobre el agua.', 5);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('8499f1d8-fbc0-4180-b3d9-6867576e00d8', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Día 3 – HANÓI - BAHÍA DE HALONG (Desayuno,  almuerzo y cena).', 6);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('ef7f730e-311d-4762-896b-ae54f547ad5b', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Después del desayuno, encuentro con nuestro guía en el hall del hotel.', 7);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('a8cd23a2-710d-467d-8534-8930dd7cdd8d', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Salida por carretera hacia la Bahía de Halong que significa “el dragón que desciende del mar” en vietnamita, y según la leyenda, fue un dragón quien formó las islas de la bahía. Embarque en un maravilloso crucero con el que visitarán la bahía. Almuerzo a bordo.', 8);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('d4f87f32-fa21-4602-853f-39ee99d210f6', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Acabado el almuerzo, continuaremos navegando y descubriendo miles de islas e islotes de abundante vegetación que emergen en la bahía con sus insólitas formas y diferentes tamaños. Las aguas color esmeralda de este legendario tesoro nos llevan a explorar islas sublimes como la de la Tortuga, la del Perro, la Cabeza de Hombre, etc. Debido a su singular belleza, peculiaridad geológica, riqueza biológica, importancia cultural e histórica, la Bahía de Halong fue declarada Patrimonio de la Humanidad por la UNESCO en 1994 e incluida en la lista de las Siete Maravillas Naturales del Mundo desde 2011.', 9);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('95d9182b-1bfa-464c-bfb6-7ce3b794dba3', '424f228a-7fc6-4ad1-9bc6-67e2d00533ca', 'Más allá de la contemplación del magnífico paisaje, disfrutamos de tiempo libre o de algunas de las actividades opcionales tales como nadar, practicar kayak o participar en una demostración de cocina vietnamita en la terraza del barco.', 10);
