INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('8a1cbaea-4234-48e0-8311-aec451feef42', 'c3e06106-518d-46da-b1db-4228b126be43', 'Almuerzo en restaurante local.', 1);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('8a8d4a76-3334-4ee8-99dc-285c4c996023', 'c3e06106-518d-46da-b1db-4228b126be43', 'Después del almuerzo, tendremos una experiencia con las flores y su tradicional forma de adornarlas. Visita a una floristería artesanal para ver cómo se realizan los ramos, cómo se transmite y conserva este hermoso y tradicional arte espiritual de Vietnam que ha llegado a nuestros días generación tras generación. Los invitados aprenderán a reconocer flores exóticas a la vez que disfrutan preparando una ofrenda floral espiritual. Posteriormente llegamos al lago Hoan Kiem, el corazón de Hanói, donde daremos un paseo alrededor del lago con una vista panorámica al templo Ngoc Son, situado en medio del lago, junto con el puente rojo The Huc.Por último, realizaremos un paseo panorámico en ciclo pousse por el Barrio Antiguo de Hanói, también conocido como el barrio de las 36 calles ya que en su tiempo fue conocido por el oficio de los artesanos que las habitaban y por los talleres que allí había.', 2);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('93181448-2051-442d-8faf-c73553e6de47', 'c3e06106-518d-46da-b1db-4228b126be43', 'Regreso al hotel y alojamiento en Hanói.', 3);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('a39cf85b-ed86-458a-a167-7f1b9177e8c9', 'c3e06106-518d-46da-b1db-4228b126be43', 'Régimen de comidas: Desayuno y almuerzo.', 4);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('5c647d9f-bbfb-4c1a-ab45-0b372d60b6e4', 'c3e06106-518d-46da-b1db-4228b126be43', 'Recomendaciones: Reservar un masaje tradicional o participar en el curso de cocina vietnamita. Asistencia al espectáculo Marionetas sobre el agua.', 5);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('1b34ca76-8ff2-4860-92a1-eaee94982d43', 'c3e06106-518d-46da-b1db-4228b126be43', 'Día 3 – HANÓI - BAHÍA DE HALONG (Desayuno, almuerzo y cena).', 6);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('6c317251-da46-4acf-8cab-f66985621f2a', 'c3e06106-518d-46da-b1db-4228b126be43', 'Después del desayuno, encuentro con nuestro guía en el hall del hotel.', 7);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('9af65882-b540-4af6-bc15-246127372410', 'c3e06106-518d-46da-b1db-4228b126be43', 'Salida por carretera hacia la Bahía de Halong que significa “el dragón que desciende del mar” en vietnamita, y según la leyenda, fue un dragón quien formó las islas de la bahía. Embarque en un maravilloso crucero con el que visitarán la bahía. Almuerzo a bordo.', 8);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('42e2fd24-6cd6-402a-bb22-8778045ca139', 'c3e06106-518d-46da-b1db-4228b126be43', 'Acabado el almuerzo, continuaremos navegando y descubriendo miles de islas e islotes de abundante vegetación que emergen en la bahía con sus insólitas formas y diferentes tamaños. Las aguas color esmeralda de este legendario tesoro nos llevan a explorar islas sublimes como la de la Tortuga, la del Perro, la Cabeza de Hombre, etc. Debido a su singular belleza, peculiaridad geológica, riqueza biológica, importancia cultural e histórica, la Bahía de Halong fue declarada Patrimonio de la Humanidad por la UNESCO en 1994 e incluida en la lista de las Siete Maravillas Naturales del Mundo desde 2011.', 9);
INSERT INTO public.inclusions (id, destination_id, item, display_order) VALUES ('f488d685-26af-491b-8cc6-36d5aa845a00', 'c3e06106-518d-46da-b1db-4228b126be43', 'Más allá de la contemplación del magnífico paisaje, disfrutamos de tiempo libre o de algunas de las actividades opcionales tales como nadar, practicar kayak o participar en una demostración de cocina vietnamita en la terraza del barco.', 10);
