INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('3e91938f-27fb-4243-ba6b-809cf4c1bd10', 'c43a0ba6-b5e0-4726-85b7-a802b4d192b7', 2, 'DUBÁI) (Desayuno y cena).', NULL, 'Desayuno en el hotel. Mañana libre. Alrededor de las 15:00 a 15:30 horas. los recogerán para realizar la excursión más popular Los Land- Cruisers (6 personas por vehículo) realizaremos un excitante trayecto por las fantásticas altas dunas donde podrán tomar fotografías únicas de la puesta de sol árabe. Una vez que se oculte el sol detrás de las dunas de arena dorada, nos dirigiremos a un campo en el Desierto. El olor a la fresca brocheta, el cordero a la parrilla, las hogueras, las tradicionales pipas de agua y los relajantes sonidos de la música árabe nos invitan a pasar una tarde inolvidable. Tras la suntuosa cena disfrutaremos del antiguo arte de la Danza del Vientre. (Se encuentran incluidos: pintarse con henna, agua, refrescos, te y café). Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('375fa8a1-ea31-4d3c-b6a8-922861042f45', 'c43a0ba6-b5e0-4726-85b7-a802b4d192b7', 3, '– DUBÁI (Desayuno).', NULL, 'Desayuno en el hotel. A la hora prevista traslado al aeropuerto de Dubai. Fin de Servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('027b4d14-1bf6-48fd-afc6-a35f8f05d3b6', 'e5a2c7c1-1953-4d88-a677-7eb944fe8611', 1, 'DUBAI', NULL, 'Llegada al aeropuerto de Dubai, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('b089ef18-7b73-4186-aa25-e7a831e6d111', 'e5a2c7c1-1953-4d88-a677-7eb944fe8611', 2, 'DUBAI (Desayuno).', NULL, 'Desayuno. Visita por la ciudad Salida hacia Deira pasando por el Zoco de las especies, Zoco de Oro; atravesando el Canal por Abra (Taxi Acuático) llegada y visita al Museo de Dubai. Por la carretera de Jumeirah, vista de la Mezquita de Jumeirah; parada para fotos en el Burj al Arab único hotel en el mundo de 7 estrellas. Pasaremos por Burj Khalifa el edificio más alto del mundo situado en el Dubai Mall (el Mall más grande del mundo con 1000 tiendas. Regreso al hotel. Alojamiento. Tarde libre.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('2ff38ee4-1899-4e54-8bfd-6e99000ed6a0', 'e95e6dde-a4ad-462a-afa5-0e7d667a920d', 3, '– EL CAIRO (Desayuno y almuerzo).', NULL, 'Desayuno en el hotel. Tendremos una visita de día completo a la ciudad de El Cairo: El Museo de Arte Faraónico, la Ciudadela de Saladino con su Mezquita de Alabastro, el Mercado de Khan el Khalili y el Barrio Copto. Regreso al hotel en El Cairo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('ad84f301-ad11-4ff0-9fe9-7de840bfce0d', '9aeb1692-fe16-4a7a-8317-3b3155db5fbf', 1, '– ATENAS.', NULL, 'Traslado aeropuerto / hotel con asistencia en español. Alojamiento con desayuno en Atenas.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('04e707ce-c1e1-44cf-ab03-adac4ce79aaf', '9aeb1692-fe16-4a7a-8317-3b3155db5fbf', 2, 'ATENAS (Desayuno).', NULL, 'Visita de la ciudad con Acrópolis en privado con guía en español (incluyendo la entrada y auriculares) Alojamiento con desayuno en Atenas', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('67c61cc8-3d9b-4718-a6a6-9248d5b98057', '9aeb1692-fe16-4a7a-8317-3b3155db5fbf', 3, '– ATENAS – MYKONOS', NULL, 'Traslado hotel / puerto del Pireo con asistencia en español Tiquete de barco ferry Atenas / Mykonos en clase economy Llegada a Mykonos y traslado al hotel con asistencia en español. Alojamiento con desayuno en Mykonos', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('c3b11c66-aa97-4037-a02a-a79993b726b3', '9aeb1692-fe16-4a7a-8317-3b3155db5fbf', 4, '– MYKONOS – SANTORINI', NULL, 'Traslado hotel / puerto de Mykonos con asistencia en español. Tiquete de barco catamarán Mykonos / Santorini en clase economy Llegada a Santorini y traslado al hotel con asistencia en español. Alojamiento con desayuno en Santorini', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('bebfb155-0ce2-403c-ab19-df463382578f', '9aeb1692-fe16-4a7a-8317-3b3155db5fbf', 5, '– SANTORINI.', NULL, 'Tour de 5 horas en Santorini con guía en español. El tour incluye incluye Fira (la capital de Santorini), el pueblo tradicional de Pyrgos y visita del pueblo de Oia para admirar el famoso atardecer. Al finalizar el tour, traslado al aeropuerto con asistencia en español.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('fb73876f-9318-4821-8801-ff3de3d967f1', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 1, 'LLEGADA A BANGKOK', NULL, 'Llegada al aeropuerto de Bangkok donde los espera su guía de habla hispana y traslado al hotel. Tiempo libre hasta el check-in en el hotel según disponibilidad (normalmente las habitaciones están disponibles a partir de las 15.00h). Alojamiento en hotel Servicio Fast Track a la llegada Aeropuertos de Suvarnabhumi (BKK) Numero de personas Neto por persona, USD - Encuentro con el asistente en el puente aéreo, le acompañan al mostrador de inmigración. le llevarán la maleta y la acompañarán hasta encontrarse con el guía. - El proceso de tiempo se basa en un caso standard, cuando los pasajeros no requieren un control de salud o visado a la llegada. - NO incluye el visado, 2000 THB.-, por favor prepare el importe en efectivo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('67c29eeb-8611-4148-94a4-7efc860804e3', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 2, 'BANGKOK', NULL, 'Después del desayuno, Visita a tres de los templos budistas más populares de la ciudad. Empezaremos por el Wat Traimit, situado en el extremo de Chinatown, en Yaowarat Road, cerca de la estación de tren Hualampong. Wat Traimit alberga el Buda de oro macizo más grande del mundo, midiendo casi cinco metros de altura con un peso de cinco toneladas y media. La excursión continuará hacia Wat Pho, el templo más grande de Bangkok. El templo del enorme Buda reclinado y los Chedis de los Reyes. Este se encuentra detrás del Templo del Buda. Es uno de los mayores templos de la ciudad y famoso por su gigantesco Buda reclinado que mide 46 metros de largo y está cubierto de oro. A continuación, visitarán el Palacio Real, que es sin duda, el monumento más famoso de la ciudad. Construido en 1782, por 150 años fue la casa del rey de Tailandia, la corte real y la sede administrativa del gobierno. El Gran Palacio de Bangkok es un edificio antiguo que continua impresionando a sus visitantes por su hermosa arquitectura y detalles. Dentro del complejo, se encuentra Wat Phra Kaew o el Templo del Buda Esmeralda (oficialmente conocido como Wat Phra Sri Rattana Satsadaram), considerado como el templo budista más importante de Tailandia, consagra el Buda más reverenciado tallado en un solo bloque de jade. Traslados incluidos. Alojamiento en hotel', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a59733fa-8239-4d58-8bdd-8e2fe886ddba', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 3, 'BANGKOK – CHIANG RAI', NULL, 'Después del desayuno traslado al aeropuerto para tomar el vuelo hacia Chiang Rai Llegada a Chiang Rai con el vuelo no más tarde que las 10.00 AM. Proceder a Chui Fong, una hermosa cultivadora de té. En un ambiente rodeado de plantaciones en las laderas de pequeñas colinas, podrán disfrutar de varias delicias como helado de té, torta de té (a gasto propio) y un almuerzo en el restaurante local cercano. Próximamente, pasaremos por un museo de opio antes de salir hacia el distrito de Mae Chan al norte de Chiang Rai. Luego continuaremos a pie, subiendo por las colinas a través de calles angostas para visitar una aldea de las minorías étnicas Akha y Yao. Nuestro último destino del día será una de las aldeas Karen, donde conoceremos a esta famosa tribu montañera – el grupo étnico minoritario más grande de Tailandia. Esta tribu originaria de Tíbet emigró a lo que hoy se conoce como Myanmar hace unos dos mil años. Durante el siglo XVIII, se estima que el conflicto político y la persecución fomentaron su migración masiva hacia el norte de Tailandia, donde todavía al día de hoy se encuentran sin un estado residencial oficial. La tribu es más reconocida por las mujeres Kayan – un subgrupo de los Po (Karen rojo) – cuyos cuellos están adornados en anillos de latón. A veces hasta empezando desde los cinco años, la presión constante empuja sus clavículas y costillas hacia abajo, dando la impresión de un cuello extendido; la razón detrás de su apodo ‘mujeres jirafa’ – una vista asombrosa. Al concluir, traslado de regreso al hotel. Cena y alojamiento en hotel.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('c12fadfa-9ea7-43f2-a102-7c0f492b34a8', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 4, 'CHIANG RAI – CHIANG MAI', NULL, 'Desayuno en el hotel. En la mañana nos trasladaremos al muelle y tomarnos un placentero paseo en bote tradicional por el río Kok visitando las tribus Karen que viven en cabañas de bambú en plena selva. Continuaremos con la visita al Templo Wat Rong Suea Tean, también conocido como el Templo Azul, otro templo budista moderno inusual que se distingue por su intenso color azul y sus estatuas elaboradas. Seguidamente visitaremos el famoso templo blanco de Wat Rong Khun. Después de la visita nos dirigiremos desde Chiang Rai a Chiang Mai por carretera (3 Hrs). Llegada a Chiang Mai y almuerzo en restaurante local. Visita al complejo de templos Wat Doi Suthep, el más conocido de Chiang Mai, situado en la cima de una pequeña colina a 15 Kms al noroeste de la ciudad. Cean y alojamiento en hotel', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('5dfe3776-7988-4dc5-80c0-b6947e17c159', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 5, 'CHIANG MAI', NULL, 'Desayuno en el hotel. Por la mañana visitaremos algunas fábricas de artesanías locales .También se visita una fábrica de esculturas de madera donde es posible comprar antigüedades birmanas. Salida hacia el valle de Mae Sa visitando la granja de las orquídeas. Almuerzo en restaurante local. Después nos trasladaremos al santuario de elefantes para aprender sobre estos animales, y realizar diversas actividades incluso darles comida y tomar un baño, una experiencia inolvidable. Cena Kantoke. Regreso al hotel. Alojamiento en el hotel.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('adea8b50-7a8b-4dd1-9489-2f25a4dbb222', 'a6d65452-944e-4cd6-bfb4-9086b9fc7b14', 6, 'CHIANG MAI', NULL, 'Desayuno en el hotel. Traslado al aeropuerto de Chiang Mai para conectar con el vuelo a su próximo destino. La solicitud de cambio de la habitación está sujeta a disponibilidad y puede acarrear costes extras.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('213e002e-ed7b-409f-bade-5ce7c707a925', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 1, 'LLEGADA A BANGKOK', NULL, 'Llegada al aeropuerto de Bangkok y encuentro con nuestro guía de habla hispana quien los estará esperando para asistirlos con el traslado a su hotel. Realizar el check-in según disponibilidad (Las habitaciones normalmente se habilitan a partir de las 15:00, pero a veces suelen facilitarse antes). Resto del día libre a su disposición. Alojamiento en el hotel seleccionado Servicio Fast Track a la llegada Aeropuertos de Suvarnabhumi (BKK) Número de personas Neto por persona, USD - Encuentro con el asistente en el puente aéreo, le acompañan al mostrador de inmigración. le llevarán la maleta y la acompañarán hasta encontrarse con el guía. - El proceso de tiempo se basa en un caso standard, cuando los pasajeros no requieren un control de salud o visado a la llegada. - NO incluye el visado, 2000 THB. -, por favor prepare el importe en efectivo (¡Visa a la llegada es solo para ciudadanos mexicanos!)', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('919dcb32-d663-4f44-a684-07b391b9edc4', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 2, 'BANGKOK', NULL, 'Después del desayuno, Visita a dos de los templos budistas más inusuales de la ciudad. Empezaremos por Wat Traimit, situado en el extremo de Chinatown en la calle Yaowarat, cerca de la estación de tren Hualampong. Wat Traimit alberga el Buda de oro macizo más grande del mundo, midiendo casi cinco metros de altura con un peso de cinco toneladas y media. La excursión continuará hacia Wat Pho, el templo más grande de Bangkok, donde se encuentra el enorme Buda reclinado y los Chedis de los Reyes. Es uno de los mayores templos de la ciudad, famoso por su gigantesco Buda reclinado que mide 46 metros de largo y está cubierto de oro. Traslados incluidos. Alojamiento en el hotel. El Gran Palacio es uno de los monumentos más famoso de la ciudad. Construido en 1782, sirvió por 150 años como la casa del rey de Tailandia, la corte real y la sede administrativa del gobierno. El Gran Palacio de Bangkok es un edificio antiguo que continúa impresionando a sus visitantes con su hermosa arquitectura y detalles intricados. Dentro del complejo, se encuentra Wat Phra Kaew – ‘el Templo del Buda Esmeralda’ (oficialmente Wat Phra Sri Rattana Satsadaram) – considerado el templo budista más importante de Tailandia. Es aquí que se consagra la imagen de buda más reverenciada del país, meticulosamente tallada en un solo bloque de jade. Neto por persona, USD', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('138b3cf2-e3e8-4144-9d9a-cfa480d8ad53', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 3, 'BANGKOK – AYUTTHAYA – ANG THONG – SUKHOTHAI', NULL, 'Después del desayuno, salida desde Bangkok hacia el norte. Haremos la primera parada en la ciudad de Ayutthaya – la antigua capital del reino de Siam y centro arqueológico del país – donde visitaremos sus templos y restos de sus fortificaciones más icónicas. Declarada como patrimonio de la humanidad por la UNESCO en 1991, Ayutthaya permanece un lugar mágico e imperdible para los amantes de la historia y civilizaciones antiguas. Próximamente, nos dirigiremos hacia la ciudad de Ang Thong donde podremos visitar el templo Wat Muang, famoso debido a que alberga la estatua de buda sentado más grande de Tailandia (novena mayor del mundo) con casi 100 metros de altura. Continuación hacia Sukhothai – Almuerzo, paisajes naturales Durante el trayecto seremos testigos del cambio en el paisaje, que va enverdeciendo y poniendose cada vez más frondoso; adelantando ya los paisajes selváticos que cubren la mayoría del norte del país. Llegaremos al alojamiento en Sukhothai por la tarde.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('1d12da85-f2fa-4981-af9e-062ea4189a0c', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 4, 'SUKHOTHAI – CHIANG RAI', NULL, 'Desayuno en el hotel. El punto más destacado del día de hoy es el Parque Arqueológico de Sukhothai, declarado como un Patrimonio de la Humanidad por UNESCO debido a su increíble belleza y exposición de varios siglos de prosperidad de la civilización tailandesa (demostrado a través de los gran monumentos y templos sofisticados erigidos en la zona). Una vez finalizada visita, nos dirigiremos a Chiang Rai vía Lampang mientras disfrutamos de las maravillosas vistas y el famoso lago Payao. Almuerzo por el camino. Llegada al alojamiento en Chiang Rai por la tarde.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('ed8f76d1-fc94-4e55-a29b-f07449367f12', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 5, 'CHIANG RAI – CHIANG MAI', NULL, 'Desayuno en el hotel. Visita al “Triángulo de Oro” del río Mekong, que abarca zonas de Tailandia, Laos y Birmania antiguamente dedicadas al tráfico del opio. Una vez allí, aprovecharemos para realizar una visita al museo Casa del Opio ubicado en la población de Chiang Rai. Luego nos dirigiremos al novedoso y llamativo Wat Rong Suea Ten (Templo azul), donde se pueden encontrar pinturas con un estilo similar al Templo Blanco, ya que fue allí donde se formó su arquitecto durante años. Parada en el espectacular y contemporáneo Wat Rong Khun, cuyo color blanco significa la pureza y el cristal representa la sabiduría de Buda como la "luz que brilla en el mundo y el universo". Retomando el viaje hacia Chiang Mai, almorzaremos por el camino. Llegada a Chiang Mai por la tarde; alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('bab41002-8727-40bd-ab90-81f6866cc55b', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 6, 'CHIANG MAI', NULL, 'Desayuno en el hotel. Por la mañana partimos hacia el conocido Wat Phra That Doi Suthep; un templo magnífico ubicado a 1000 metros de elevación en las laderas de la montaña Doi Suthep. Requiriendo una subida de 306 escalones con barandillas de serpientes Naga, este templo sagrado ofrece una vista panorámica espectacular de la ciudad, así como una gran variedad de imágenes y frescos a aquellos que estén dispuestos a emprender la escalada. Almuerzo en restaurante local. Por la tarde, nos dirigiremos al Santuario de Elefantes para aprender sobre estos animales y realizar diversas actividades incluyendo dándoles de comer y bañándolos – una experiencia inolvidable. Traslado de regreso al hotel, resto de la tarde libre a su disposición.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('436cdabf-471c-4f40-b8e4-ab55652088f0', '9ed8e3da-1381-476e-9fa9-b9bc9906daf6', 7, 'CHIANG MAI – SALIDA', NULL, 'Desayuno en el hotel. A la hora indicada, traslado al aeropuerto para tomar su vuelo hacia su siguiente destino.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('812169cb-61c6-4f94-98d4-d7ef63f367ec', 'e95e6dde-a4ad-462a-afa5-0e7d667a920d', 4, '– EL CAIRO -SANTA CATALINA (Desayuno y cena).', NULL, 'Desayuno. Salida hacia la península del Sinaí vía el túnel de Ahmed Hamdy por debajo del canal de Suez, pasando de África a Asia. Visitaremos Ayun Musa o las fuentes de Moisés (Mara) para luego seguir hacia la ciudad de Santa Catalina. Llegada al hotel. Cena y alojamiento en Santa Catalina.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('298ff5d2-5571-44e7-9ad3-989aaafb7a7b', 'e95e6dde-a4ad-462a-afa5-0e7d667a920d', 5, '– SINAI (Desayuno y almuerzo).', NULL, 'Por la mañana temprano, subir el Monte Sinaí, donde podemos ver a la hora de la madrugada un hermoso espectáculo. Descenso de la Montaña. Regreso al hotel, tomar el desayuno y descanso. Visita al monasterio de Santa Catalina. Traslado a la frontera de Taba con Israel y fin de nuestro servicio.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('5c9d06d0-6cc3-4ab5-a700-bc35bbb44148', 'de9eb689-7a5f-4095-a254-57e4d4276338', 1, 'Llegada a Cusco / City Tour Arqueológico', NULL, 'Traslado a aeropuerto, vuelo a Cusco, llegada y traslado a hotel. Por la tarde, recojo de hotel para inicio del City Tour visitando Sacsayhuaman, Kenko, Puka Pukará y Tambo Machay. Fin del tour en Plaza San Francisco.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('1e3fde7b-8cd1-4a8b-814b-6c2e0af1da29', 'de9eb689-7a5f-4095-a254-57e4d4276338', 2, 'Tour Valle Sagrado – Maras - Moray', NULL, 'Recojo en hotel e inicio de tour. Visitas a Chinchero (pueblo), Salineras de Maras, Moray. Almuerzo incluido. Visita a Ollantaytambo y Pisaq. Retorno a Cusco. Fin del tour en Plaza San Francisco.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('22bf3bb5-b2ce-4a18-987d-a2b5d5dd8e75', 'de9eb689-7a5f-4095-a254-57e4d4276338', 3, 'Tour Machu Picchu', NULL, 'Recojo de hotel, salida en tren hacia Aguas Calientes desde Ollantaytambo. Llegada a Aguas Calientes y reunión de grupo. Inicio de Tour a Machu Picchu. Fin del tour y retorno a Aguas Calientes. Partida en tren hacia Ollantaytambo y luego traslado a Cusco. Fin del tour en Plaza San Francisco.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('51aca756-3615-43a1-9b9b-cf3da8c8b1c3', 'de9eb689-7a5f-4095-a254-57e4d4276338', 4, 'Vuelo de Retorno', NULL, 'Partida hacia Aeropuerto. Llegada a Lima y vuelo de salida.', NULL, NULL, NULL);
