INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('5a6728a9-42c7-40a4-aed6-04d185101663', 'c3e06106-518d-46da-b1db-4228b126be43', 4, 'VIE: BAHÍA DE HALONG - VUELO A DA NANG - HOI AN', NULL, 'A la salida del sol y para aquellos que estén interesados hay una clase de Tai chi en la terraza solárium. Continuamos navegando por la bahía de casi 2000 islas de roca calcárea y disfrutando de sus paisajes únicos. Aprovechar este increíble momento para sacar las mejores fotos de esas maravillas. Tendremos un buen brunch para recargar baterías y emprender el retorno a tierra. Desembarcamos en el muelle de Halong, desde donde nos trasladamos a Hanói por carretera hasta el aeropuerto para tomar el vuelo a Da Nang. A su llegada, tenemos el traslado directo hasta Hoi An (aprox. 30 min) Al anochecer, traslado al río Hoai, por donde navegaremos Este río tiene un significado muy especial para los habitantes de Hoi An, ya que ha sido testigo de los acontecimientos acaecidos en Vietnam a través de los años. Cuando cae la noche, las calles se iluminan con farolillos y luces de colores, también se lanzan linternas al agua para pedir buenos augurios. La ciudad luce un nuevo aspecto brillante y misterioso. Desembarcar y tiempo libre para pasear por la calle y volver al hotel por su cuenta. Alojamiento en Hoi An. Notas: Duración del vuelo a Danang, 1h 10min aprox.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a87d217a-35b3-436e-9c47-94e080574796', 'e5a2c7c1-1953-4d88-a677-7eb944fe8611', 3, '–DUBAI', NULL, 'Desayuno en el hotel. Mañana libre. Alrededor de las 15:00 a 15:30 hrs. los recogerán para realizar la excursión más popular Los Land Cruisers (6 personas por vehículo) realizaremos un excitante trayecto por las fantásticas altas dunas donde podrán tomar fotografías únicas de la puesta de sol árabe. Una vez que se oculte el sol detrás de las dunas de arena dorada, nos dirigiremos a un campo en el Desierto. El olor a la fresca brocheta, el cordero a la parrilla, las hogueras, las tradicionales pipas de agua y los relajantes sonidos de la música árabe nos invitan a pasar una tarde inolvidable. Tras la suntuosa cena disfrutaremos del antiguo arte de la Danza del Vientre. (Se encuentran incluidos: Ski por la arena, pintarse con henna, agua, refrescos, te y café). Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('2eff4df9-ea09-46f0-8109-d8212a626fef', 'e5a2c7c1-1953-4d88-a677-7eb944fe8611', 4, '– DUBÁI (Desayuno).', NULL, 'Desayuno en el hotel. A la hora prevista traslado al aeropuerto de Dubai. Fin de Servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('4847fdc3-7f96-4bcc-87d1-2d0b0bfc7d49', 'c3e06106-518d-46da-b1db-4228b126be43', 5, 'SAB: HOI AN – VISITAS', NULL, 'Después del desayuno, empezamos la visita de la ciudad de Hoi An, un importante puerto comercial de Asia en los siglos XVII y XVIII, cuya arquitectura y relajado estilo de vida han cambiado poco en los últimos años. Llegada al Barrio Antiguo, desde donde empezaremos el paseo a pie por el centro de la ciudad antigua para visitar Phung Hung (Antigua casa de los mercaderes), el puente japonés cubierto con más de 400 años de antigüedad, la sala de Phuc Kien, la antigua Casa Tan Ky con su arquitectura tradicional y el museo. Almuerzo en restaurante. Tarde libre para disfrutar de la playa, pasear por el colorido mercado del centro o realizar compras. Alojamiento en Hoi An.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('1e530481-0d2a-4a4e-a898-64350b5119b2', 'c3e06106-518d-46da-b1db-4228b126be43', 6, 'HOI AN - DA NANG - VUELO DE SALIDA', NULL, 'Desayuno y traslado al aeropuerto para tomar el vuelo hacia su próximo destino. ******Fin de nuestros servicios*****', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('6ba0b9b2-064b-491b-b94f-cbacbfeebef2', '4c5a7fae-1432-48fe-9b16-0b318e4aaa33', 1, 'DUBAI', NULL, 'Llegada al aeropuerto de Dubái, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('7bee8545-eb2f-44fa-961f-53617270901d', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 1, 'Estambul', NULL, 'Llegada al aeropuerto y traslado al hotel con asistente de habla hispana. Alojamiento en el Pullman Istanbul Airport and Convention Center Hotel o similar.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('f764c844-39c8-4bf4-b238-3760a3463794', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 2, 'Estambul', NULL, 'Desayuno. Día libre. Excursión opcional: Paseo Bósforo + Gálata (con almuerzo). Visita al Bazar Egipcio. Crucero por el Bósforo, que separa Asia de Europa. Vistas de Ortakoy, la Mezquita Mecidiye y los puentes colgantes. Uso del Túnel. Visita a la Torre de Gálata. Subida al Morro do Café de Pierre Loti.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('ed7d5276-5c31-4ac4-a8e8-5da50c52e49a', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 3, 'Estambul - Ankara - Capadocia', NULL, 'Desayuno y salida hacia Ankara. Recorrido panorámico con parada en el mausoleo de Ataturk. Almuerzo. Continuación hacia Capadocia. Cena y alojamiento en el Ramada by Wyndham Cappadocia o similar. Suplemento de vuelo Estambul - Capadocia disponible.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a9f11838-c405-4051-841c-655e996b5107', '4c5a7fae-1432-48fe-9b16-0b318e4aaa33', 2, 'DUBAI (Desayuno y cena).', NULL, 'Desayuno. Visita por la ciudad Salida hacia Deira pasando por el Zoco de las especies, Zoco de Oro; atravesando el Canal por Abra (Taxi Acuático) llegada y visita al Museo de Dubái. Por la carretera de Jumeirah, vista de la Mezquita de Jumeirah; parada para fotos en el Burj al Arab único hotel en el mundo de 7 estrellas. Pasaremos por Burj Khalifa el edificio más alto del mundo situado en el Dubái Mall (el Mall más grande del mundo con 1000 tiendas). Regreso al hotel. Por la noche salida a las 19:30 hrs. Para disfrutar de las vistas y los sonidos de la cala de Dubái navegando 2 horas abordo en un Dhow tradicional. El viaje se realiza desde la desembocadura del arroyo, a lo largo del mismo hasta el iluminado Dubái Creek Golf Club que asemeja a una vela de barco. Cena incluida. Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a4171748-157f-47b1-ae6d-a5fad985f7ff', '4c5a7fae-1432-48fe-9b16-0b318e4aaa33', 3, '–DUBAI (Desayuno y cena).', NULL, 'Desayuno en el hotel. Mañana libre. Alrededor de las 15:00 a 15:30 hrs. los recogerán para realizar la excursión más popular Los Land Cruisers (6 personas por vehículo) realizaremos un excitante trayecto por las fantásticas altas dunas donde podrán tomar fotografías únicas de la puesta de sol árabe. Una vez que se oculte el sol detrás de las dunas de arena dorada, nos dirigiremos a un campo en el Desierto. El olor a la fresca brocheta, el cordero a la parrilla, las hogueras, las tradicionales pipas de agua y los relajantes sonidos de la música árabe nos invitan a pasar una tarde inolvidable. Tras la suntuosa cena disfrutaremos del antiguo arte de la Danza del Vientre. (Se encuentran incluidos: Ski por la arena, pintarse con henna, agua, refrescos, te y café). Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('6312d54c-f106-4ee1-9b31-57a358498e7e', 'e95e6dde-a4ad-462a-afa5-0e7d667a920d', 2, 'EL CAIRO – (Desayuno y almuerzo).', NULL, 'Desayuno. Salida para realizar la visita incluida a las tres Pirámides de Giza, la eterna Esfinge y el Templo del Valle (no incluye entrada al interior de las Pirámides). Por la tarde Realizaremos una visita a la necrópolis de Sakkara y la ciudad de Menfis, capital del imperio antiguo. Opcional:- Por la noche podemos ir a disfrutar de un Espectáculo de Luz y Sonido de las Pirámides. Regreso al hotel en El Cairo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('1514e24a-69b5-4f81-b929-705f6d858f13', '4c5a7fae-1432-48fe-9b16-0b318e4aaa33', 4, '– DUBAI / VISITA ABU DHABI / DUBAI (Desayuno y almuerzo).', NULL, 'Desayuno. Visita a Abu Dhabi. Recorrido de Dubái pasando por Puerto Jebel Ali, el puerto más grande del mundo realizado por los hombres hasta la capital de UAE. (2 horas aproximadas). Admiraremos la Mezquita del Jeque Zayed la tercera más grande del mundo; así como la tumba del mismo, antiguo presidente de UAE y padre de la nación. Continuación hasta el puente de Al Maqta pasando por una de las áreas más ricas de Abu Dhabi, el área de los ministros. Llegada a la calle Corniche que es comparada con Manhattan. Parada para fotos en el hotel Emirates Palace. Este hotel tiene su propio helipuerto y puerto. Continuamos a Al Batee Área, donde se encuentran los palacios de la familia Real. Almuerzo en restaurante local, visita panorámica al parque de Ferrari (breve tiempo para sacar fotos y ver tiendas). Regreso a Dubái. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('3095a31c-941c-495a-81ed-41f3d5fa2f87', '4c5a7fae-1432-48fe-9b16-0b318e4aaa33', 5, '– DUBÁI (Desayuno).', NULL, 'Desayuno en el hotel. A la hora prevista traslado al aeropuerto de Dubái. Fin de Servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('98ad195d-e037-4af8-a25f-7fec9d614719', '4d6107a7-853c-48c9-ac68-75581775e8cd', 1, '– DUBAI.', NULL, 'Llegada al aeropuerto de Dubái, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('cd047227-27ea-4d93-aed9-fd5999cde835', '4d6107a7-853c-48c9-ac68-75581775e8cd', 2, 'DUBAI (Desayuno).', NULL, 'Desayuno en el hotel. City Tour Clásico de Dubái de medio día que cubre los lugares más representativos e históricos de la ciudad, como la Mezquita Jumeirah, la región de los Palacios de los jeques y el Dubái Creek - que es el canal de agua salada que atraviesa la ciudad. En la histórica fortaleza de Al Fahidi, que fue convertida en museo, se puede entender la evolución del país y profundizar en la cultura local. El tour también incluye un pintoresco paseo en la ABRA-taxi de agua, utilizado para cruzar el arroyo para conectarse a bazares típicos: el zoco de Especias y del oro. (Las entradas para el paseo en el ABRA & en el Museo están incluidas en el programa). Tour en Dubái moderno, donde visitaremos el más grande Mall del mundo, tiempo para comprar suvenires. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('e95fa449-1952-4079-89c0-f0c5ef27a3b1', '4d6107a7-853c-48c9-ac68-75581775e8cd', 3, '– DUBAI / VISITA ABU DHABI / DUBAI (Desayuno y almuerzo).', NULL, 'Desayuno en el hotel. Vamos a descubrir el impresionante paisaje de Abu Dhabi, capital de los Emiratos Árabes Unidos, también conocida como la joya de Arabia. Sheikh Zayed Grand Mosque: Esta hermosa casa de culto es uno de los lugares más espectaculares de la ciudad, es la tercera mezquita más grande del mundo; con capacidad para 40 mil personas. Toda construida en mármol de Carrara, fue inaugurada en el otoño de 2007. Su impresionante arquitectura complementa el paisaje urbano de Abu Dhabi. La visita es una oportunidad única para conocer detalles interesantes sobre la arquitectura de esta estructura y aprender más acerca de la religión y la cultura de la gente de Abu Dhabi. Continuación hasta el puente de Al Maqta pasando por una de las áreas más ricas de Abu Dhabi, el área de los ministros. Llegada a la calle Corniche que es comparada con Manhattan. Parada para fotos en el hotel Emirates Palace. Este hotel tiene su propio helipuerto y puerto. Continuamos a Al Batee Area, donde se encuentran los palacios de la familia Real. Visita, panorámica al parque de Ferrari (breve tiempo para sacar fotos y ver tiendas). Regreso a Dubái. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('7e43b25c-3aa5-44c5-a998-ae3bad4cb3eb', '4d6107a7-853c-48c9-ac68-75581775e8cd', 4, '– DUBAI/SAFARI (Desayuno y cena).', NULL, 'Desayuno en el hotel. Mañana libre para descubrir los encantos de esta ciudad. Por la tarde salida al “safari del desierto”. Esta encantadora aventura comienza en el momento que entra en el vehículo 4x4. Las dunas y el clima desértico los llevan a otro mundo. En el medio del Tour encontramos un oasis natural y una granja de camellos, un paisaje espectacular para sacar preciosas fotos. Después de la aventura en las dunas del desierto, hay una parada para admirar la puesta de sol árabe disfrutando de la serenidad y la belleza del desierto. En el campamento beduino, el turista tendrá la oportunidad de montar en camello, fumar narguile y hacer el famoso tatuaje de henna, o simplemente disfrutar de la noche con una deliciosa barbacoa de la moda de los árabes. (Paseo en camello & barbacoa árabe están incluidas en el programa). Alojamiento', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a0874b63-24dc-4173-9256-6b1b4b247e27', '4d6107a7-853c-48c9-ac68-75581775e8cd', 5, '– DUBAI/CRUCERO DHOW.', NULL, 'Desayuno en el hotel. Mañana libre para descubrir los encantos de esta ciudad. Por la noche cena a bordo Del Dhow cruise (embarcación tradicional árabe, es ideal para un entorno verdaderamente romántico por la noche, todo iluminado y decorado, desliza a lo largo de la famosa cala de Dubái. Es difícil elegir entre la deliciosa variedad de comida que se sirve en el buffet a bordo. El rescate de la memoria nostálgica del espíritu aventurero árabe, el Dhow crucero Dubái se remonta a los días confinados a una tribu cuya economía estaba basada en la explotación y el comercio de perlas. Disfrute de una vista encantadora de la vibrante noche de Dubái y la frescura de la brisa del mar (Cena buffet incluida en el programa). Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('d0aca4fe-06c3-4070-9054-cbdd410ddce2', '4d6107a7-853c-48c9-ac68-75581775e8cd', 6, '– DUBÁI (Desayuno).', NULL, 'Desayuno en el hotel. A la hora indicada traslado al aeropuerto de DXB para tomar vuelo de salida internacional.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('652a6b74-b30e-4f4d-a8c0-b8b5826e0568', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 1, '– DUBAI.', NULL, 'Llegada al aeropuerto de Dubái, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('fe917a70-9c79-43d0-af7e-34f5b18b6f15', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 2, 'DUBAI (Desayuno).', NULL, 'Desayuno. Visita por la ciudad Salida hacia Deira pasando por el Zoco de las especies, Zoco de Oro; atravesando el Canal por Abra (Taxi Acuático) llegada y visita al Museo de Dubai. Por la carretera de Jumeirah, vista de la Mezquita de Jumeirah; parada para fotos en el Burj al Arab único hotel en el mundo de 7 estrellas. Parada torre más alta del mundo Burj Khalifa el edificio más alto del mundo situado en el Dubai Mall (el Mall más grande del mundo con 1000 tiendas). Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('d039b389-5352-44fa-9431-8ee49a12311f', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 3, '– DUBAI (Desayuno y cena).', NULL, 'Desayuno. Mañana libre. Por la noche salida a las 19:30 hrs. Para disfrutar de las vistas y los sonidos de la cala de Dubai navegando 2 horas abordo en un Dhow tradicional. El viaje se realiza desde la desembocadura del arroyo, a lo largo del mismo hasta el iluminado Dubai Creek Golf Club que asemeja a una vela de barco. Cena incluida. Regreso al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('b4edd443-fed2-4dbc-a1f6-10c20aba3faf', 'c8cb4562-e3f4-434d-b638-39494cfe9f3b', 3, '– EL CAIRO – SINAI (Desayuno, almuerzo y cena).', NULL, 'Desayuno. Salida desde El Cairo a desierto del Sinaí y el Canal de Suez. Dejando el continente africano para entrar en Asia, pasando por Oyun Musa almuerzo y Wadi Feiran. Llegada al hotel en Santa Catarina. Cena y alojamiento en el hotel.', NULL, NULL, NULL);
