INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('280a673e-b1e1-4444-8e32-e324d6cb91ac', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 5, '– DUBAI / VISITA ABU DHABI / DUBAI (Desayuno y almuerzo).', NULL, 'Desayuno. Visita a Abu Dhabi. Recorrido de Dubái pasando por Puerto Jebel Ali, el puerto más grande del mundo realizado por los hombres hasta la capital de UAE. (2 horas aproximadas). Admiraremos la Mezquita del Jeque Zayed la tercera más grande del mundo; así como la tumba del mismo, antiguo presidente de UAE y padre de la nación. Continuación hasta el puente de Al Maqta pasando por una de las áreas más ricas de Abu Dhabi, el área de los Ministros. Llegada a la calle Corniche que es comparada con Manhattan. Parada para fotos en el hotel Emirates Palace. Este hotel tiene su propio helipuerto y puerto. Continuamos a Al Batee Area, donde se encuentran los palacios de la familia Real. Almuerzo en restaurante típico. Visita panorámica al parque de Ferrari (breve tiempo para sacar fotos y ver tiendas). Regreso a Dubái. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('ca3a39fc-d2ab-4ab9-826a-331d6ed85f8d', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 6, '– DUBAI/ VISITASHARJAH / VISITA FUJEIRAH / DUBAI (Desayuno y almuerzo).', NULL, 'Desayuno. Visita al emirato de Sharjah donde visitaremos El Zoco Azul, conocido por la venta de artesanías, pasaremos por la mezquita Faisal que ha sido regalo del difunto Rey Faisal al emirato de Sharjah, seguimos hacia la Costa Este Fujairah la excursión comienza con un paseo por el paisaje del desierto a través del Oasis Al Daid a Masafi. Justo antes de llegar a Masafi, nos detendremos en el mercado local Mercado de los viernes, que ha sido creado por los comerciantes desde antaño. Se puede encontrar de todo, desde frutas, plantas de interior, juguetes, cerámicas, tapices y todo tipo de regalos. Continuación a través de las montañas de Hajar hasta bajar a las aguas azules del Golfo de Oman, y divisar la bella Dibba. Almuerzo en hotel 4* de playa, día, de regreso visita a la Mezquita Bidiyah, la mezquita más antigua de UAE. Nuestra ruta sigue a lo largo de la costa de Khorr Fakkan. En camino se pasará por Fujairah, el único emirato en la costa este, regreso al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('29f0f97c-9002-4682-a8e0-9ba8c0f27b23', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 7, '– DUBAI (Desayuno).', NULL, 'Desayuno. Día libre. Regreso al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('da0a6ae2-2d51-4c92-bfda-60ec6092e198', 'a3a1fa45-57de-4905-92ad-7fe67f091c9d', 8, '– DUBAI (Desayuno).', NULL, 'Desayuno en el hotel. A la hora prevista traslado al aeropuerto de Dubai. Fin de Servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9e79d839-8482-4d10-b065-4a94684b36eb', '4a457002-eba2-4864-96a1-0a7039b379d5', 1, '– EL CAIRO.', NULL, 'Llegada al aeropuerto, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9667a877-937d-43ee-a9ee-87d16c92f6b7', '4a457002-eba2-4864-96a1-0a7039b379d5', 2, 'EL CAIRO –LAS PIRÁMIDES Y LA GRAN ESFINGE (Desayuno).', NULL, 'Desayuno y luego comienza el día visitando a las encantadoras pirámides de Keops, Kefren y Micerinos en Giza. Visita a la Gran Esfinge, la cabeza de un faraón con cuerpo de león, que se remonta a la época de Kefren. El tour también incluye una visita al Templo del Valle que pertenece a la Pirámide de Kefren. en El Cairo. y traslado al hotel y Alojamiento. Opcional visitando la pirámide escalonada en Sakkara, diseñada por el arquitecto Imhotep para el rey Zoser. Almuerzo en un restaurante local de buena calidad.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('b144061c-1d85-425f-9a02-14a3544ffafd', '4a457002-eba2-4864-96a1-0a7039b379d5', 3, '– EL CAIRO', NULL, 'Desayuno. Día libre Opcionalmente Salida para visitar el Barrio Copto donde se encuentra la Iglesia de Santa María la Virgen, más conocida como la Iglesia Colgante, la Iglesia de San Jorge y la Iglesia de San Sergio. Continuación en el Museo de Arte Egipcio, que alberga numerosas estatuas, pinturas, relieves, elementos funerarios y otros muchos objetos de la época de los Faraones; la Mezquita de Mohamed Ali Pasha, más conocida como la Mezquita de Alabastro, la Ciudadela de Salah El aDin (Saladino, quien fuera Sultán de Egipto), declarada Patrimonio de la Humanidad y el Gran Bazar de Khan el Khalili donde podremos callejear para realizar compras o tomar un té en un café de la zona. Traslado al hotel y alojamiento..', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('17263809-91ea-4475-a664-2fac2339c90b', '4a457002-eba2-4864-96a1-0a7039b379d5', 4, '– EL CAIRO - SHAM EL SHEIHK', NULL, 'Desayuno, traslado al Aeropuerto Internacional de El Cairo y continúe. Tomará un vuelo desde El Cairo hasta el encantador destino de Sharm El Sheikh. A su llegada al aeropuerto de Sharm El Sheikh, Luego lo acompañará a su hotel para realizar el check-in sin inconvenientes. Tendrá tiempo libre por la tarde y la noche para relajarse y disfrutar de la serenidad de este paraíso costero. Pase una cómoda noche en su hotel en Sharm El Sheikh, donde le esperan la relajación y la belleza.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('fde2a7dd-f999-4290-8fc6-46b1d7966d24', '4a457002-eba2-4864-96a1-0a7039b379d5', 5, '– QUAD BIKE EN SHAM EL SHEIHK (Desayuno).', NULL, 'Desayuno, día libre Opcionalmente ¡Prepárate para una emocionante aventura! horas, tu guía turístico privado te recibirá en tu hotel. Desde allí, te dirigirás al Quad Bike Centre, que se encuentra a unos 15 minutos de viaje. Después de una breve pero importante orientación sobre seguridad e instrucciones de conducción, comenzará tu aventura en quad en Sharm. Tomarás el control de tu quad y partirás a través del fascinante desierto del Sinaí, donde podrás disfrutar de impresionantes vistas del paisaje. 20 Después de aproximadamente una hora de emocionante conducción, llegarás al punto más alto de tu viaje. Aquí, tendrás la oportunidad de tomar un merecido descanso y saborear un té de hierbas beduino tradicional, sumergiéndote en la auténtica cultura de la región. Una vez que hayas recargado energías,. Desde allí, serás trasladado de regreso a tu hotel en la comodidad de un vehículo con aire acondicionado. La noche es para que la disfrutes a tu aire. Disfruta de una tranquila noche en tu hotel de Sharm, donde podrás reflexionar sobre la emocionante aventura del día.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9b9c0887-a9a4-4e63-b30f-e4204b54ccae', 'c8cb4562-e3f4-434d-b638-39494cfe9f3b', 4, '– SINAI (Desayuno).', NULL, 'Por la mañana temprano, subir el Monte Sinaí, donde podemos ver a la hora de la madrugada un hermoso espectáculo. Descenso de la Montaña. Regreso al hotel, tomar el desayuno y descanso. Visita al monasterio de Santa Catalina. Traslado a la frontera de Taba con Israel y fin de nuestro servicio.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('bf5b156d-cf51-48a0-912e-8ec4621402f2', '4a457002-eba2-4864-96a1-0a7039b379d5', 6, '– QUAD BIKE EN SHAM EL SHEIHK (Desayuno).', NULL, 'Desayuno, día libre Opcionalmente, salida de su hotel. Lo llevarán al muelle marítimo, donde se embarcará en un barco con destino al Parque Nacional Ras Mohamed. Este extraordinario santuario marino es un paraíso para los entusiastas de las actividades submarinas, con más de 130 especies de arrecifes de coral y la asombrosa cantidad de 1000 variedades de peces vibrantes y coloridos. Durante su viaje, el barco hará tres paradas, lo que le brindará la oportunidad de participar en aventuras de natación y esnórquel en las aguas cristalinas del Mar Rojo. Se servirá un delicioso almuerzo a bordo mientras disfruta de la belleza de este mundo submarino. Al final del día, el barco regresará a la actividad de esnórquel en el Mar Rojo y lo transportarán de regreso a su hotel. Disfrute de otra cómoda estadía de una noche en su hotel de Sharm El Sheikh..', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('7e021e67-43a5-4513-9700-84870c2ef971', '4a457002-eba2-4864-96a1-0a7039b379d5', 7, '–EL CAIRO (Desayuno).', NULL, 'Desayuno en el hotel y prepárese para el check-out y trasladado al aeropuerto para tomar el vuelo de regreso al Aeropuerto Internacional de El Cairo. Después  último destino para tomar su último vuelo de regreso a casa. Fin del viaje y de nuestros servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9a1efbcc-89ff-45df-a99d-6b58e51fbf70', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 1, '– EL CAIRO.', NULL, 'Llegada al aeropuerto, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('8fbffce3-6bb4-47b7-aeb1-7803b281dab4', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 2, 'EL CAIRO –Las Pirámides y La gran Esfinge (Desayuno y almuerzo).', NULL, 'Desayuno y luego comienza el día visitando a las encantadoras pirámides de Keops, Kefren y Micerinos en Giza. Visita a la Gran Esfinge, la cabeza de un faraón con cuerpo de león, que se remonta a la época de Kefren. El tour también incluye una visita al Templo del Valle que pertenece a la Pirámide de Kefren. en El Cairo. y traslado al hotel y Alojamiento. Opcional visitando la pirámide escalonada en Sakkara, diseñada por el arquitecto Imhotep para el rey Zoser. Almuerzo en un restaurante local de buena calidad.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('5e7bcf27-bddf-4f3c-8f21-f6b4bfa97911', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 3, '– EL CAIRO –LUXOR - El Templo de Karnak - Templo de Luxor  (Pensión Completa).', NULL, 'Desayuno. Salida según plan de vuelos. Tomar el vuelo con destino al Luxor. Llegada a Luxor Traslado en vehículo privado con aire acondicionado al Crucero por el Nilo. Donde te recibirán y trasladarán para embarcar en un crucero de lujo de 5 estrellas por el Nilo. Pensión completa. Salida para visitar El Templo de Karnak erigido en honor al dios Amón y principal recinto de culto en Egipto desde las dinastías que conformaron el Imperio Nuevo y que cuenta, entre otros, con una magnífica sala hipóstila de 23 metros de altura que alberga en su recinto nada menos que 134 columnas y el Templo de Luxor, más pequeño que el de Karnak y comunicado con éste por una impresionante avenida de esfinges con cabeza de carnero, más conocida como la Avenida de las esfinges. Regreso a la motonave, y noche a bordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a5685b31-c736-402d-814f-dc8e2035d4f1', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 4, '– LUXOR - ESNA - EDFU (Pensión Completa).', NULL, 'Pensión completa. Desayuno. Salida para Visitar: el Valle de los Reyes, declarado por la Unesco Patrimonio de la Humanidad y que alberga la mayoría de las tumbas de los faraones del Imperio Nuevo. El Templo de la Reina Hatshepsut, que fue la primera mujer en ocupar la posición de faraón; el templo, situado en Deir El Bahari, es uno de los pocos templos construidos directamente en la roca y consta de varias terrazas que ejercían como vestíbulos para albergar a multitud de personas durante las festividades religiosas. Los Colosos de Memnon, gigantescas estatuas que representan al faraón Amenhotep III y que permanecen en posición sedente desde hace más de 3.500 años. Regreso a la motonave, navegación hacia Edfú y noche a bordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('afe52bc2-2eb8-43b0-911f-b1f406462ba7', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 5, '– EDFU - KOM OMBO – ASWAN (Pensión Completa).', NULL, 'Pensión completa. Desayuno. Salida para realizar la visita al Templo de Edfú, erigido en honor del dios Horus con cabeza de halcón. El templo, situado en la orilla occidental del río Nilo es uno de los más grandes de Egipto y el mejor conservado hasta el momento. Las inscripciones en sus paredes proporcionan información importante sobre el lenguaje, la mitología y la religión durante el periodo grecorromano. Regreso a la motonave y navegación. Llegada a Kom Ombo y salida para realizar la visita del único templo en Egipto dedicado simultáneamente a dos dioses, el dios Sobek con cabeza de cocodrilo y el dios Haroeris con cabeza de halcón. Regreso a la motonave, navegación y noche a bordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('20401073-8f89-479a-93dd-63ef0e4d9143', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 6, '– ASWAN (Pensión Completa).', NULL, 'Pensión completa. Desayuno.Salida para realizar la visita del Templo de Philae, construido en honor de la diosa Isis y considerado la joya del Nilo. Por la tarde, realizaremos un paseo en faluca o motora por el Nilo. (En caso de que por falta de tiempo no pueda realizarse en Aswan, el paseo se realizará en El Cairo o en Luxor). Regreso a la motonave y noche a bordo. Opcional: Posibilidad de realizar por carretera la visita opcional a los majestuosos Templos de Abu Simbel. El templo de Abu Simbel, junto con el templo de Nefertari forma el complejo de Abu Simbel, y fue mandado erigir por el Faraón Ramsés II, uno en su propio honor y otro en el de su esposa. Son uno de los seis templos edificados en roca en la zona nubia y tuvieron que ser trasladados de su emplazamiento original tras la construcción de la gran presa de Aswan.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('46375486-ff07-483e-8bb3-70e9bef5f62d', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 7, '– ASWAN - EL CAIRO (Desayuno).', NULL, 'Desayuno y día libre. A la hora prevista desembarque y traslado al aeropuerto para volar con destino a El Cairo. Recepción y traslado al hotel y alojamiento. Opcional: Salida para visitar el Barrio Copto donde se encuentra la Iglesia de Santa María la Virgen, más conocida como la Iglesia Colgante, la Iglesia de San Jorge y la Iglesia de San Sergio. Continuación en el Museo de Arte Egipcio, que alberga numerosas estatuas, pinturas, relieves, elementos funerarios y otros muchos objetos de la época de los Faraones; la Mezquita de Mohamed Ali Pasha, más conocida como la Mezquita de Alabastro, la Ciudadela de Salah El aDin (Saladino, quien fuera Sultán de Egipto), declarada Patrimonio de la Humanidad y el Gran Bazar de Khan el Khalili donde podremos callejear para realizar compras o tomar un té en un café de la zona. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('408dca5c-e0f7-4a43-b126-5cd44c67b40a', '04b9e2be-dd06-42bb-9e36-a1f9698b3e39', 8, '– EL CAIRO (Desayuno).', NULL, 'Desayuno. A la hora convenida traslado al aeropuerto  y según plan de vuelos, con destino España. Llegada. Fin del viaje y de nuestros servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('6c1ffe3a-c805-4ff2-b531-9266aa5af4d0', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 1, '– EL CAIRO.', NULL, 'Llegada al aeropuerto, asistencia de habla hispana fuera del aeropuerto por parte de nuestro representante. Traslado al hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('4eb29c8a-72fa-4d85-ae03-0358a6f9322a', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 2, 'EL CAIRO – EL CAIRO –LAS PIRÁMIDES Y LA GRAN ESFINGE (Desayuno y almuerzo).', NULL, 'Desayuno y luego comienza el día visitando a las encantadoras pirámides de Keops, Kefren y Micerinos en Giza. Visita a la Gran Esfinge, la cabeza de un faraón con cuerpo de león, que se remonta a la época de Kefren.El tour también incluye una visita al Templo del Valle que pertenece a la Pirámide de Kefren. en El Cairo. Traslado al hotel y alojamiento (Almuerzo en un restaurante local.) Opcional: Continuación en de Arte Egipcio, Entre los preciados objetos de la colección del El Museo Egipcio se pueden ver estatuas, pinturas, relieves y elementos funerarios entre otros numerosos objetos, aunque si hay dos áreas que destacan sobre el resto de las exposiciones se trata de las salas de Tutankamón, donde se exponen los tesoros que se encontraron en su tumba, y la sala de las momias, donde reposan los restos momificados de importantes faraones. Después La Ciudadela de Salah El Al Din (Saladino, quien fuera Sultán de Egipto), a Ciudadela de Saladino en El Cairo En árabe es Qalaat Salaḥ ad-Din. Es una fortaleza grande en el Cairo. Construido entre los años 1176 y 1183 para proteger la ciudad.Fue una fortificación medieval islámico en El Cairo, Egipto. En Mokattam colina cerca del centro de El Cairo, fue famoso por su brisa fresca y grande vistas de la ciudad. Ahora es un sitio históricoconservado, con mezquitas y museos en su interior y traslado al hotel y Alojamiento. Comidas: Desayuno, Almuerzo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('2039e13c-eb15-4e71-84e2-43fe8b88db0b', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 3, 'EL CAIRO – ASWAN  (Pensión Completa).', NULL, 'Desayuno. Traslado Aeropuerto para tomar el vuelo hacia Aswan donde te recibirán y trasladarán para embarcar en un crucero por el Nilo. Cena y noche abordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('b2c3183b-0c59-4d92-bc42-8a32fe483052', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 4, '– ASUÁN - KOM OMBO – EDFU (Pensión Completa).', NULL, 'Pensión completa. Desayuno. Opcional: Posibilidad de realizar por carretera la visita opcional a los majestuosos Templos de Abu Simbel. El templo de Abu Simbel, junto con el templo de Nefertari forma el complejo de Abu Simbel, y fue mandado erigir por el Faraón Ramsés II, uno en su propio honor y otro en el de su esposa. Son uno de los seis templos edificados en roca en la zona nubia y tuvieron que ser trasladados de su emplazamiento original tras la construcción de la gran presa de Aswan. Salida para realizar la visita del Templo de Philae, construido en honor de la diosa Isis y considerado la joya del Nilo. Finalizada la visita, daremos un paseo en faluca o motora por el Nilo. (En caso de que por falta de tiempo no pueda realizarse en Aswan, el paseo se realizará en El Cairo o en Luxor).Navegación hacia Kom Ombo. Llegada y salida para realizar la visita del único templo en Egipto dedicado simultáneamente a dos dioses, el dios Sobek con cabeza de cocodrilo y el dios Haroeris con cabeza de halcón. Regreso a la motonave, navegación y noche a bordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('a2f85b1f-eed0-4868-94dd-b3e391c5a722', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 5, '– EDFU - ESNA - LUXOR (Pensión Completa).', NULL, 'Pensión completa. Desayuno. Salida para realizar la visita al Templo de Edfú, erigido en honor del dios Horus con cabeza de halcón. El templo, situado en la orilla occidental del río Nilo es uno de los más grandes de Egipto y el mejor conservado hasta el momento. Las inscripciones en sus paredes proporcionan información importante sobre el lenguaje, la mitología y la religión durante el periodo grecorromano. Regreso a la motonave y navegación hacia Luxor atravesando la esclusa de Esna. Llegada a Luxor y salida para realizar las visitas del Templo de Karnak erigido en honor al dios Amón y principal recinto de culto en Egipto desde las dinastías que conformaron el Imperio Nuevo y que cuenta, entre otros, con una magnífica sala hipóstila de 23 metros de altura que alberga en su recinto nada menos que 134 columnas y el Templo de Luxor, (por calesa con caballos) ,para ver panorámica del Templo de Luxor por la noche más pequeño que el de Karnak y comunicado con éste por una impresionante avenida de esfinges con cabeza de carnero, más conocida como la Avenida de las esfinges. Regreso a la motonave y noche a bordo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('ed4ce056-474c-44f0-9ed2-0a305c3f9756', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 6, '– LUXOR - HURGHADA (Pensión Completa).', NULL, 'Pensión completa. Desayuno. Opcional: Posibilidad de realizar PASEO EN GLOBO AEROSTÁTICO Paseo en globo durante el amanecer en Luxor: una sensación indescriptible: Luxor desde el aire: experimente el amanecer con un paseo en globo aerostático. Los vuelos en globo aerostático son una experiencia increíble, y qué mejor destino para Antigüedades. Desembarque y salida para finalizar las visitas de Luxor: El Valle de los Reyes, declarado por la Unesco Patrimonio de la Humanidad y que alberga la mayoría de las tumbas de los faraones del Imperio Nuevo continuación hasta Los Colosos de Memnon, gigantescas estatuas que representan al faraón Amenhotep III y que permanecen en posición sedente desde hace más de3.500 años.Terminadas las visitas, traslado por autobús con el aire acondicionado hasta Hurghada. Llegada al hotel y alojamiento en régimen de todo Incluido.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('376d58c1-e92f-44f1-8247-c9cf05480876', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 7, '– HURGADA (Desayuno).', NULL, 'Desayuno. Disfrutando de la playa. Opcional puede unirse a uno de los increíbles viajes visitar Orange Bay o Paradise Island. Viaje con barco.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9e4450e1-cd05-4b2b-b94c-204b19549cc9', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 8, '– HURGADA (Pensión Completa).', NULL, 'Desayuno. Disfrutando de la playa. Opcional por la tarde Safari beduino atardecer disfrutando con el espectáculo beduino y danza folclórica. Comidas: Desayuno, Almuerzo y Cena', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('317906cd-3c1d-4b64-af2b-fc7221e0680d', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 9, '– HURGADA – EL CAIRO (Pensión Completa).', NULL, 'Desayuno. Salida para regresar al Cairo traslado por autobús con el aire acondicionado hasta el Cairo. Paseo nocturno por la ciudad del Cairo conocer la ciudad antigua y el centro del Cairo y cena (plato de KUSHARI típico egipcio y probar FALAFEL) Traslado al hotel y alojamiento. Comidas: Desayuno, Almuerzo y Cena', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('9a992eff-36bb-4aa9-9373-29cd22c5bb69', '88be8f27-1395-44ba-9f5b-58ac9ea9f387', 10, '– EL CAIRO (Desayuno).', NULL, 'Desayuno. A la hora convenida traslado al aeropuerto y según plan de vuelos, con destino España. Llegada. Fin del viaje y de nuestros servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('4b4b6f87-4af5-4416-bf0c-6456531a3d8c', 'c8cb4562-e3f4-434d-b638-39494cfe9f3b', 1, '– EL CAIRO.', NULL, 'Llegada al Aeropuerto Internacional de El Cairo. Visa, de inmigración y aduanas con asistencia de nuestro representante. Traslado al hotel. Cena y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('999cf235-f828-4751-bbf9-1957e2deb35a', 'c8cb4562-e3f4-434d-b638-39494cfe9f3b', 2, 'EL CAIRO – (Desayuno, almuerzo y cena).', NULL, 'Desayuno. Por la mañana, visita a las Pirámides de Giza (Keops, Kefren y Micerinos), la Esfinge y el templo del valle de Kefren almuerzo. Por la tarde, visitar El Museo Egipcio para ver las obras maestras de la época faraónica y los tesoros del Rey Tut. Regreso al hotel. Cena y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('7454d684-0e5f-4a4c-b5b6-5363ba4df79d', 'e95e6dde-a4ad-462a-afa5-0e7d667a920d', 1, '– EL CAIRO.', NULL, 'Llegada al Aeropuerto de El Cairo, nuestro representante lo recibirá y lo asistirá antes de que el control de pasaportes en el aeropuerto. Luego será trasladado por un coche privado A / C a su hotel. Alojamiento en El Cairo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('3c08b3c7-d7c5-4a1e-ae1b-afd193701f97', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 4, 'Capadocia', NULL, 'Desayuno. Visita a la ciudad subterránea de Ozkonak. Por la tarde, visita al Museo abierto de Góreme (capillas y frescos del siglo X). Visita a Avanos (alfombras artesanales). Visita a una joyería y tiendas de piedras típicas. Cena y alojamiento. Excursiones opcionales: Noche Turca (danza del vientre y folclórica) y Paseo en Globo.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('caead6d7-9930-4757-81ae-137b66d49783', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 5, 'Capadocia', NULL, 'Desayuno. Visita a Avcilar y Guvercinlik (paisaje de "Fairy Chimney"). Visita a la ciudad troglodita de Uchisar. Por la tarde, visita a una cooperativa de cerámica. Cena y alojamiento. Excursión Opcional Cappa Park (a partir de Abr 15): Monster Safari, Zipline, Jet Boat. Excursión Opcional de Invierno (Dic 15 a Mar 31): Erciyes Ski. Excursión opcional: Almuerzo o cena en SkyDinner (a 50 metros de altitud).', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('20a2ddbf-dd96-47be-b8f3-f435f6524a03', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 6, 'Capadocia - Pamukkale', NULL, 'Desayuno y salida hacia Pamukkale. Visita a una tienda Outlet. Visita a Hierapolis y el "Castelo de Algodão" (formaciones de piedra caliza con piscinas naturales blancas). Cena y alojamiento en el Richmond Pamukkale Thermal o similar.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('f92b1d04-0536-44bd-92b5-fc831e1e21e7', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 7, 'Pamukkale - Éfeso - Izmir', NULL, 'Desayuno. Salida para visitar las ruinas de Éfeso (Biblioteca de Celso, Calle de Mármol, Teatro). Visita a La Casa Virgen María. Visita a un outlet de pieles. Cena y alojamiento en el Hampton by Hilton Izmir Aliaga o similar.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('e24ba6a0-58c8-443f-b73b-fdb8e6059535', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 8, 'Esmirna - Bursa - Estambul', NULL, 'Desayuno. Salida hacia Bursa. Parada para visitar una tienda de delicias turcas ("Turkish Delight"). Visitas panorámicas de la ciudad. Visita a la Mezquita Verde y el Mausoleo Verde. Paseo por el mercado de la seda. Regreso a Estambul. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('c3f0aa91-e1e7-45ac-bc47-8c746139d3ce', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 9, 'Estambul', NULL, 'Desayuno. Día libre. Alojamiento. Excursión opcional - Estambul Clásico con almuerzo: Palacio de Topkapi, Hagia Sophia (mezquita), Mezquita Azul, Gran Bazar.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('6c1043ff-a1ca-4cca-a15a-fe830c4d633c', '5099c499-78ed-453c-9fd7-d0cae36d5e6d', 10, 'Estambul', NULL, 'Desayuno y traslado al aeropuerto para tomar el vuelo de regreso. Fin de nuestros servicios.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('7da1f48c-a26c-4aec-ab94-1e680b9962eb', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 1, 'Estambul', NULL, 'Llegada al aeropuerto y traslado al hotel con un asistente de habla portuguesa / española. Llegada al hotel. Alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('b3627d41-d4de-4806-b120-bf437992976d', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 2, 'Estambul', NULL, 'Desayuno en el hotel. Salida para una visita panorámica. Recorrido por una de las zonas más antiguas. Pasaremos por las murallas que rodean la ciudad, construidas en el siglo V y que han sido destruidas y reconstruidas cuatro veces. Durante todo el recorrido podrá disfrutar de la maravillosa vista del Bósforo. El recorrido continúa por Dolmabahce, el estadio Beeiktae, Taksim, Siehane y Eminonu. Nota: Los pasajeros que no adquieran la visita panorámica posterior opcional dispondrán de tiempo libre y regresarán al hotel por su cuenta.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('4eed1370-23bb-47b9-aa93-987970a606fd', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 3, 'Estambul - Ancara - Capadócia', NULL, 'Suplemento no obligatorio de vuelo interno İST-CAP. Desayuno en el hotel y salida hacia Ankara. Llegada y visita panorámica de la ciudad con parada en el mausoleo de Ataturk. Continuación hacia Capadocia. Llegada al hotel. Cena en el hotel y alojamiento.', NULL, '{Desayuno,Cena}', NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('f6ffd0cf-1fe5-4643-a568-1c66ff5d34ce', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 4, 'Capadócia', NULL, 'Desayuno en el hotel. Salida para visitar la ciudad subterránea de Ozkonak, construida por comunidades cristianas que necesitaban protegerse de los romanos. Por la tarde, visita al Museo Abierto de Goreme, que alberga numerosas capillas y casas talladas en piedra y decoradas con frescos del siglo X, algunos de los cuales representan a San Jorge, que nació allí. Visita a Avanos, tradicional por sus famosas alfombras hechas a mano con una técnica ancestral, donde tendremos la oportunidad de visitar una cooperativa especializada. Por la tarde visitaremos una joyería de piedras típicas de Capadocia. Cena en el hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('28cead95-2f81-406c-84a3-a982557deb8a', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 5, 'Capadócia', NULL, 'Desayuno en el hotel y visita a Avcilar y Guvercinlik, donde podrá disfrutar del paisaje único de la «Chimenea de las Hadas» con vistas a Capadocia. Visita a la ciudad troglodita de Uchisar en el punto más alto de Capadocia. Por la tarde, visita a una cooperativa de cerámica de Capadocia. Cena en el hotel y alojamiento.', NULL, '{Desayuno,Cena}', NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('d6e56d9e-c68d-4fd5-bc37-46d8ff469ef7', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 6, 'Capadócia - Pamukkale', NULL, 'Desayuno en el hotel y salida hacia Pamukkale. Visita a una tienda Outlet. Llegada a Pamukkale. Visita de Hierápolis y del "Castillo de Algodón", una verdadera maravilla natural, con su increíble formación calcárea y sus piscinas blancas naturales. Cena en el hotel y alojamiento.', NULL, NULL, NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('abd7f9dd-09de-4162-88a6-81c66e0340a6', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 7, 'Pamukkale - Éfeso - Esmirna', NULL, 'Desayuno en el hotel y salida para visitar las ruinas de Éfeso, la antigua ciudad grecorromana mejor conservada de Turquía. Fue la capital asiática del Imperio Romano. Podrá ver la magnífica Biblioteca Celso y la Calle de Mármol, así como el teatro con capacidad para 25.000 personas. Aquí es donde San Pablo habló a sus seguidores. Almuerzo en un restaurante local. Visita de la Casa de la Virgen María, lugar religioso cristiano cerca de Éfeso, a siete kilómetros de Selcuk, donde, según la tradición, Juan el Evangelista llevó a la Virgen María después de la crucifixión de Cristo, huyendo de la persecución en Jerusalén, e incluso su bendita asunción, según los ortodoxos. Todavía tendremos tiempo de visitar un excelente punto de venta de cuero de alta calidad procedente de esta región. Cena en el hotel y alojamiento.', NULL, '{Desayuno,Cena}', NULL);
INSERT INTO public.itinerary_days (id, destination_id, day_number, title, location, description, activities, meals, accommodation) VALUES ('810ee13c-880b-4085-9faa-277b7434823f', 'a0edb8c2-7e77-444e-8221-2501fe87f338', 8, 'Esmirna - Bursa - Estambul', NULL, 'Desayuno en el hotel y salida hacia Bursa. Parada para visitar una tienda de exquisiteces turcas, las famosas Delicias Turcas. Visita panorámica de la ciudad que fue capital del Imperio Otomano antes de Edirne. Visita de la Mezquita Verde, el Mausoleo Verde, que fue construido a petición del Sultán Mehmet I. La mezquita está decorada con azulejos de Iznik de colores verde y turquesa. Mehmet I y su dinastía están enterrados en el Mausoleo Verde, que forma parte del complejo de la mezquita. En Bursa también tendremos la oportunidad de pasear por el mercado de la seda, donde se pueden comprar antigüedades, sedas, perfumes y pashminas. Regreso a Estambul. Llegada al hotel, alojamiento.', NULL, '{Desayuno,Almuerzo}', NULL);
